import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';

function Dashboard() {
    const navigate = useNavigate();
    const [activeTool, setActiveTool] = useState('');

    const handleSelectTool = (tool) => {
        setActiveTool(tool);
        navigate(`/tools/${tool}`);
    };

    return (
        <div className="dashboard-container">
            <h1>Dashboard</h1>
            <button
                className={activeTool === 'salessearcher' ? 'active' : ''}
                onClick={() => handleSelectTool('salessearcher')}
            >
                Sales Searcher
            </button>
            <button
                className={activeTool === 'oosspotter' ? 'active' : ''}
                onClick={() => handleSelectTool('oosspotter')}
            >
                OOS Spotter
            </button>
            <button
                className={activeTool === 'ordersync' ? 'active' : ''}
                onClick={() => handleSelectTool('ordersync')}
            >
                Order Sync
            </button>
        </div>
    );
}

export default Dashboard;
